import axios from "axios";
import { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";

function Comment() {
  const location = useLocation();
  let postId = location.pathname.substring(6);
  const [comments, setComments] = useState([]);
  const getComments = async () => {
    try {
      axios
        .get(`http://localhost:8800/api/user/comments/${postId}`)
        .then((res) => setComments(res.data));
    } catch (err) {
      console.log(err);
    }
  };
  useEffect(() => {
    getComments(postId);
  }, []);
  return (
    <div className="app">
      <div className="container">
        <div className="comment">
          <h2>Comment</h2>
          {comments.map((comment) => (
            <div className="block-comment">
              <div className="user">
                {comment.userImg && <img src={comment.useImg} />}
                <h3>this is name of user</h3>
              </div>
              <div className="comment-content">this is content of comment</div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Comment;
