import axios from "axios";
import { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";

function Comment() {
  const location = useLocation();
  const [idPost, setIdPost] = useState("");
  const [comments, setComments] = useState([]);
  const getComments = async (idPost) => {
    try {
      let res = axios.get(`http://localhost:8800/api/user/comments/${idPost}`);
      console.log((await res).data);
    } catch (err) {
      console.log(err);
    }
  };
  useEffect(() => {
    setIdPost(location.pathname.substring(6));
    getComments(idPost);
  }, [idPost]);
  return (
    <div className="app">
      <div className="container">
        <div className="comment">
          <h2>Comment</h2>
          <div className="block-comment">
            <div className="user">
              <img src="http://localhost:3000/upload/posts/1717004270317internimg14.png" />
              <h3>this is name of user</h3>
            </div>
            <div className="comment-content">this is content of comment</div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Comment;
