import axios from "axios";
import { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";

function Comment() {
  const location = useLocation();
  const [comments, setComments] = useState([]);
  let postId = location.pathname.substring(6);

  const getComments = async () => {
    let url = `http://localhost:8800/api/user/comments/${postId}`;
    try {
      const res = await axios.get(url);

      if (res.status === 300) {
        // Xử lý khi nhận được response 300
        const choices = res.data; // Giả sử response.data chứa các lựa chọn
        if (choices.length > 0) {
          // Chọn một URL từ các lựa chọn (bạn có thể thêm logic chọn lựa phù hợp ở đây)
          const chosenUrl = choices[0];
          const chosenRes = await axios.get(chosenUrl);
          setComments(chosenRes.data); // Cập nhật comments với dữ liệu từ URL đã chọn
        }
      } else {
        // Xử lý response thành công (status khác 300)
        setComments(res.data);
      }
    } catch (err) {
      console.error("Error fetching comments:", err);
      if (err.response) {
        console.error("Response data:", err.response.data);
        console.error("Response status:", err.response.status);
        console.error("Response headers:", err.response.headers);
      }
    }
  };
  useEffect(() => {
    getComments();
  }, [postId]);

  return (
    <div className="app">
      <div className="container">
        <div className="comment">
          <h2>Comment</h2>
          {comments.map((comment) => (
            <div className="block-comment" key={comment.id}>
              <div className="user">
                {comment.userImg && <img src={comment.userImg} alt="User" />}
                <h3>{comment.userName}</h3>
              </div>
              <div className="comment-content">{comment.content}</div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Comment;
