  return (
    <div className="menu">
      <h1>Other posts may you like</h1>
      {posts.map((post) => (
        <div className="post">
          <img src={`../upload/posts/${post.img}`} />
          <h2>{post.title}</h2>
          <Link to={`/post/${post.id}`}>Read more</Link>
        </div>
      ))}
    </div>
  );
}

export default Menu;
