import { Link, useLocation } from "react-router-dom";
import Header from "../components/Header.js";
import Footer from "../components/Footer.js";
import Menu from "../components/Menu.js";
import axios from "axios";
import moment from "moment";

import { useContext, useEffect, useState } from "react";

function Single() {
  const [post, setPost] = useState([]);
  const location = useLocation();
  const postId = location.pathname.split("/")[2];
  const { currentUser } = useContext();
  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await axios.get(`http://localhost:8800/api/post/${postId}`);
        console.log(res);
        setPost(res.data);
      } catch (err) {
        console.log(err);
      }
    };
    fetchData();
  }, [postId]);
  return (
    <div className="app">
      <div className="container">
        <Header />
        <div className="single">
          <div className="content">
            <img src={post?.img} alt="" />
            <div className="user">
              <img src="" />
              <div className="info">
                <span>{post.username}</span>
                <p>Posted {moment(post.date).fromNow()} </p>
              </div>
              <div className="edit">
                <Link to="">
                  <img src="../upload/delete.png" alt="" />
                </Link>
                <img src="../upload/edit.png" alt="" />
              </div>
            </div>
            <h1>{post.title}</h1>
            <p>
              Lorem, ipsum dolor sit amet consectetur adipisicing elit. A
              possimus excepturi aliquid nihil cumque ipsam facere aperiam at!
              Ea dolorem ratione sit debitis deserunt repellendus numquam ab vel
              perspiciatis corporis!
            </p>
          </div>
          <Menu />
        </div>
        <Footer />
      </div>
    </div>
  );
}

export default Single;
