import { Link, useLocation } from "react-router-dom";
import Header from "../components/Header.js";
import Footer from "../components/Footer.js";
import { useEffect, useRef, useState } from "react";
import axios from "axios";
function Home() {
  const [posts, setPosts] = useState([]);
  const [textSearch, setTextSearch] = useState("");
  const searchInput = useRef();
  const cat = useLocation();
  const fetchData = async () => {
    try {
      const res = await axios.get(
        `http://localhost:8800/api/post/${cat.search}`
      );
      setPosts(res.data);
    } catch (err) {
      console.log(err);
    }
  };
  const handleSearchPosts = (e) => {
    console.log(e);
    if (e.key == "Enter") {
      if (searchInput.current.value == "") {
      } else {
        const newPosts = posts.filter((item) => {
          let title = item.title;
          return (
            title
              .toLowerCase()
              .indexOf(searchInput.current.value.toLowerCase()) != -1
          );
        });
        setTimeout(() => {
          setPosts(newPosts);
        }, 500);
      }
    } else {
      const newPosts = posts.filter((item) => {
        let title = item.title;
        return (
          title
            .toLowerCase()
            .indexOf(searchInput.current.value.toLowerCase()) != -1
        );
      });
      setTimeout(() => {
        setPosts(newPosts);
        setTextSearch("");
      }, 300);
    }
  };
  useEffect(() => {
    fetchData();
  }, [cat]);
  const getText = (html) => {
    const doc = new DOMParser().parseFromString(html, "text/html");
    return doc.body.textContent;
  };
  return (
    <>
      <div className="app">
        <div className="container">
          <Header />
          <div className="form-control">
            <input
              value={textSearch}
              type="text"
              ref={searchInput}
              onKeyDown={handleSearchPosts}
              placeholder="Enter your search"
            />
            <button onClick={handleSearchPosts}>Enter</button>
          </div>
          <div className="home">
            <div className="posts">
              {posts.map((post) => (
                <div className="post" key={post.id}>
                  <div className="img">
                    <img src={`../upload/posts/${post.img}`} alt="" />
                  </div>
                  <div className="content">
                    <Link className="link" to={`/post/${post.id}`}>
                      <h1>{post.title}</h1>
                    </Link>
                    <div className="limted-text" id="limited-text">
                      {getText(post.description).substring(0, 180) + "..."}
                    </div>
                    <button>
                      <Link to={`/post/${post.id}`}>Read More</Link>
                    </button>
                  </div>
                </div>
              ))}
            </div>
          </div>
          <Footer />
        </div>
      </div>
    </>
  );
}

export default Home;
