import Header from "../components/Header.js";
import Footer from "../components/Footer.js";
import { useContext, useState } from "react";
import { AuthContext } from "../context/authContext.js";
function EditProfile() {
  const { currentUser } = useContext(AuthContext);
  const [username, setUsername] = useState(currentUser.username);
  const [email, setEmail] = useState(currentUser.email);
  const [userImg, setUserImg] = useState(currentUser.userImg);
  const chooseImg = document.getElementById("userImg");
  const handleChangeUserImg = () => {
    if (userImg == null) {
      return `
      `;
    }
  };
  return (
    <div className="app">
      <div className="container">
        <Header />
        <h2>Your info account</h2>
        <form>
          <div className="form-control">
            <label htmlFor="username">Your account name:</label>
            <input
              onChange={(e) => setUsername(e.target.value)}
              value={`${username}`}
              name="username"
            />
          </div>
          <div className="form-control">
            <label htmlFor="email">Your email:</label>
            <input
              onChange={(e) => setEmail(e.target.value)}
              value={`${email}`}
              name="email"
            />
          </div>
          <div className="form-control" id="userImg">
            <label></label>
            <input type="file" />
            {handleChangeUserImg()}
          </div>
        </form>
        <Footer />
      </div>
    </div>
  );
}

export default EditProfile;
